import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [skills, setSkills] = useState([]);
  const [projects, setProjects] = useState([]);
  const [achievements, setAchievements] = useState([]);


  useEffect(() => {
    axios.get('http://localhost:5000/skills')
      .then((response) => {
        console.log('Skills:', response.data);
        setSkills(response.data);
      })
      .catch((error) => console.error('Error fetching skills:', error));

    axios.get('http://localhost:5000/project')
      .then((response) => {
        setProjects(response.data);
      })
      .catch((error) => console.error('Error fetching projects:', error));

    axios.get('http://localhost:5000/achievements')
      .then((response) => {
        setAchievements(response.data);
      })
      .catch((error) => console.error('Error fetching achievements:', error));
  }, []);

  return (
    <div className="App">
      <h1>Vaishnavi's Portfolio</h1>

      <section>
        <h2>Skills</h2>
        <ul>
          {skills.map(skill => (
            <li key={skill.ID}>{skill.Name} - {skill.Level}</li>
          ))}
        </ul>
      </section>

      <section>
        <h2>Projects</h2>
        <ul>
          {projects.map(project => (
            <li key={project.id}>
              <a href={project.link} target="_blank" rel="noopener noreferrer">{project.name}</a>: {project.description}
            </li>
          ))}
        </ul>
      </section>

      <section>
        <h2>Achievements</h2>
        <ul>
          {achievements.map(achievement => (
            <li key={achievement.id}>{achievement.title}: {achievement.description}</li>
          ))}
        </ul>
      </section>
    </div>
  );
}

export default App;
